SELECT i.REST_ID, i.REST_NAME, i.FOOD_TYPE, i.FAVORITES, i.ADDRESS, round((select avg(re.REVIEW_SCORE) from REST_REVIEW re where re.REST_ID=i.REST_ID),2) as SCORE from REST_INFO i where i.ADDRESS like "서울%" and round((select avg(re.REVIEW_SCORE) from REST_REVIEW re where re.REST_ID=i.REST_ID),2) is not null order by SCORE desc, i.FAVORITES desc