SELECT member_name, review_text, date_format(review_date, "%Y-%m-%d") review_date from rest_review r join member_profile m on r.member_id=m.member_id where r.member_id in (select member_id from rest_review group by member_id having count(member_id)=(select count(re.member_id) c from rest_review re group by member_id order by count(re.member_id) desc limit 1)) order by review_date, review_text
                         